  // Method to get SVG widget with color
  static Widget getIcon(String svgString, {Color? color}) {
    return SvgPicture.string(
      svgString,
      colorFilter: color != null ? ColorFilter.mode(color, BlendMode.srcIn) : null,
      width: 24,
      height: 24,
    );
  }
